function productSlider(){let t=new MasterSlider;t.control("arrows",{insertTo:"#slider_arrow_front",autohide:!1}),t.setup("slider_front",{width:730,height:880,autoHeight:!1,space:0,loop:!0,view:"flow",layout:"autofill",speed:20})}function productImageLook(){const t=document.querySelector(".product-content__image");t.addEventListener("mouseenter",(()=>{gsap.to(t,{duration:.3,autoAlpha:0})})),t.addEventListener("mouseleave",(()=>{gsap.to(t,{duration:.3,autoAlpha:1})}))}function initMain(){productImageLook()}function initMainMobile(){}document.body.clientWidth>820||screen.width>820?initMain():initMainMobile();