function productSlider(){let e=new MasterSlider;e.control("arrows",{insertTo:"#slider_arrow_front",autohide:!1}),e.setup("slider_front",{width:730,height:880,autoHeight:!1,space:0,loop:!0,view:"flow",layout:"autofill",speed:20})}const productImage=document.querySelector(".product-content__image"),productOptions=document.querySelector(".product-content__options"),productGallery=document.querySelector(".product-content__gallery_inside"),productViewIcon=document.getElementById("productViewIcon");function productImageLook(){let e=new gsap.timeline({reversed:!0});return e.to([productImage,productOptions],{duration:.3,autoAlpha:0}).from(productGallery,{duration:.2,delay:"-0.2",autoAlpha:0,scale:.97}),productViewIcon.addEventListener("click",(()=>{e.play()})),productGallery.addEventListener("click",(()=>{e.reversed()?e.restart():e.reverse()})),e}function initMain(){productImageLook()}function initMainMobile(){}document.body.clientWidth>820||screen.width>820?initMain():initMainMobile();